package com.IfElse;

public class CheckVowelOrConsonanat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		char ch = 'i';
		if (ch == 'a' || ch == 'u' || ch == 'i' || ch == 'o' || ch == 'u') {
			System.out.println( ch+"  is Vowel");
		} else {
			System.out.println( ch+ "is Consonanat");
		}

	}

}
