package com.demo.datatype;

public class SquareRoot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int var1=10;
        //double var1=10.0;
        double res=Math.pow(var1, 2);
        System.out.println("power of 10 is:"+res);
        
        double root=Math.sqrt(49);
        System.out.println("Squre root:"+root);
        
        double result=Math.sqrt(5.0);
        System.out.println("Squre root:"+result);
	}

}
